memberdef.initializer:
bool operator==(const NeoFOAM::Field<NF_TYPE>& nf, const Foam::Field<OF_TYPE>& of)             \
{                                                                                              \
    auto nfHost = nf.copyToHost();                                                             \
    auto nfSpan = nfHost.span();                                                               \
    for (int i = 0; i < nfSpan.size(); i++)                                                    \
    {                                                                                          \
        if (nfSpan[i] != convert(of[i]))                                                       \
        {                                                                                      \
            return false;                                                                      \
        }                                                                                      \
    }                                                                                          \
    return true;                                                                               \
};
